import { useEffect } from 'react';

import './App.css';
import searchIcon from './search.svg';

//static variable making to call the api

const API_URL = 'https://www.omdbapi.com?apikey=517fab5c';

//THATS CALL API URL

const movie1 = {
  Title: 'Batman: The Animated Series',
  Year: '1992–1995',
  imdbID: 'tt0103359',
  Type: 'series',
  Poster:
    'https://m.media-amazon.com/images/M/MV5BOTM3MTRkZjQtYjBkMy00YWE1LTkxOTQtNDQyNGY0YjYzNzAzXkEyXkFqcGdeQXVyOTgwMzk1MTA@._V1_SX300.jpg'
};

const App = () => {
  const searchMovies = async (title) => {
    //callapi
    const response = await fetch(`${API_URL}&s=${title}`);
    const data = await response.json();

    console.log(data);
  };

  useEffect(() => {
    searchMovies('Batman');
  }, []);
  //now stating jsx of app starting with div
  return (
    <div className="app">
      <h1>MoviePlanet</h1>

      <div className="search">
        <input
          placeholder="Search for Movies"
          value="Batman"
          onChange={() => {}} //callbackfn
        />
        <img src={searchIcon} alt="search" onClick={() => {}} />
      </div>

      <div className="container">
        <div className="movie">
          <div>
            <p>{movie1.Year}</p>
          </div>
          <div>
            <img src={movie1.Poster} alt={movie1.Title} />
          </div>
        </div>
      </div>
    </div>
    // or agar movie poster na dikhe to 
    //<img src={movie1.Poster  is nit equal t !=='N/A' ?movie1.Poster agr image nhi h fir :'https:/' />
    //yaha input m 2 chize crucial hai value ab hum value dalnge use dalne k satg hmra j value ki input hai wo searcg bar m dikh jyegi or hategi nhi iske lie hum callback functiob lenge onchange taki input dale to change ho jaye
  );
};

export default App;
